(window.webpackJsonp=window.webpackJsonp||[]).push([[102],{245:function(a,t,r){"use strict";r.r(t);var e=r(0),v=Object(e.a)({},(function(){var a=this,t=a.$createElement,r=a._self._c||t;return r("ContentSlotsDistributor",{attrs:{"slot-key":a.$parent.slotKey}},[r("h1",{attrs:{id:"jvm内存结构解析"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#jvm内存结构解析"}},[a._v("#")]),a._v(" JVM内存结构解析")]),a._v(" "),r("Badge",{attrs:{text:"JAVA",vertical:"middle"}}),a._v(" "),r("Badge",{attrs:{text:"JVM",vertical:"middle"}}),a._v(" "),r("Badge",{attrs:{text:"2018-12-24",type:"warning",vertical:"middle"}}),a._v(" "),r("h2",{attrs:{id:"内存结构图"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#内存结构图"}},[a._v("#")]),a._v(" 内存结构图")]),a._v(" "),r("p",[r("img",{attrs:{src:"/images/pasted-54.png",alt:"upload successful"}})]),a._v(" "),r("p",[a._v("Java 的自动内存管理主要是针对对象内存的回收和对象内存的分配。同时，Java 自动内存管理最核心的功能是 堆 内存中对象的分配与回收。")]),a._v(" "),r("h2",{attrs:{id:"java堆（heap）"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#java堆（heap）"}},[a._v("#")]),a._v(" Java堆（Heap）")]),a._v(" "),r("p",[a._v("对于大多数应用来说，Java堆（Java Heap）是Java虚拟机所管理的内存中最大的一块。Java堆是被所有线程共享的一块内存区域，在虚拟机启动时创建。此内存区域的唯一目的就是存放对象实例，几乎所有的对象实例都在这里分配内存。")]),a._v(" "),r("p",[a._v("Java堆是垃圾收集器管理的主要区域，因此很多时候也被称做“GC堆”。如果从内存回收的角度看，由于现在收集器基本都是采用的分代收集算法，所以Java堆中还可以细分为：新生代和老年代；再细致一点的有Eden空间、From Survivor空间、To Survivor空间等。")]),a._v(" "),r("p",[a._v("根据Java虚拟机规范的规定，Java堆可以处于物理上不连续的内存空间中，只要逻辑上是连续的即可，就像我们的磁盘空间一样。在实现时，既可以实现成固定大小的，也可以是可扩展的，不过当前主流的虚拟机都是按照可扩展来实现的（通过-Xmx和-Xms控制）。")]),a._v(" "),r("p",[a._v("如果在堆中没有内存完成实例分配，并且堆也无法再扩展时，将会抛出OutOfMemoryError异常。")]),a._v(" "),r("h2",{attrs:{id:"方法区（method-area）"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#方法区（method-area）"}},[a._v("#")]),a._v(" 方法区（Method Area）")]),a._v(" "),r("p",[a._v("方法区（Method Area）与Java堆一样，是各个线程共享的内存区域，它用于存储已被虚拟机加载的类信息、常量、静态变量、即时编译器编译后的代码等数据。虽然Java虚拟机规范把方法区描述为堆的一个逻辑部分，但是它却有一个别名叫做Non-Heap（非堆），目的应该是与Java堆区分开来。")]),a._v(" "),r("p",[a._v("对于习惯在HotSpot虚拟机上开发和部署程序的开发者来说，很多人愿意把方法区称为“永久代”（Permanent Generation），本质上两者并不等价，仅仅是因为HotSpot虚拟机的设计团队选择把GC分代收集扩展至方法区，或者说使用永久代来实现方法区而已。")]),a._v(" "),r("p",[a._v("Java虚拟机规范对这个区域的限制非常宽松，除了和Java堆一样不需要连续的内存和可以选择固定大小或者可扩展外，还可以选择不实现垃圾收集。相对而言，垃圾收集行为在这个区域是比较少出现的，但并非数据进入了方法区就如永久代的名字一样“永久”存在了。这个区域的内存回收目标主要是针对常量池的回收和对类型的卸载，一般来说这个区域的回收“成绩”比较难以令人满意，尤其是类型的卸载，条件相当苛刻，但是这部分区域的回收确实是有必要的。")]),a._v(" "),r("p",[a._v("根据Java虚拟机规范的规定，当方法区无法满足内存分配需求时，将抛出OutOfMemoryError异常。")]),a._v(" "),r("h2",{attrs:{id:"程序计数器（program-counter-register）"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#程序计数器（program-counter-register）"}},[a._v("#")]),a._v(" 程序计数器（Program Counter Register）")]),a._v(" "),r("p",[a._v("程序计数器（Program Counter Register）是一块较小的内存空间，它的作用可以看做是当前线程所执行的字节码的行号指示器。在虚拟机的概念模型里（仅是概念模型，各种虚拟机可能会通过一些更高效的方式去实现），字节码解释器工作时就是通过改变这个计数器的值来选取下一条需要执行的字节码指令，分支、循环、跳转、异常处理、线程恢复等基础功能都需要依赖这个计数器来完成。\n由于Java虚拟机的多线程是通过线程轮流切换并分配处理器执行时间的方式来实现的，在任何一个确定的时刻，一个处理器（对于多核处理器来说是一个内核）只会执行一条线程中的指令。因此，为了线程切换后能恢复到正确的执行位置，每条线程都需要有一个独立的程序计数器，各条线程之间的计数器互不影响，独立存储，我们称这类内存区域为“线程私有”的内存。\n如果线程正在执行的是一个Java方法，这个计数器记录的是正在执行的虚拟机字节码指令的地址；如果正在执行的是Natvie方法，这个计数器值则为空（Undefined）。")]),a._v(" "),r("p",[a._v("此内存区域是唯一一个在Java虚拟机规范中没有规定任何OutOfMemoryError情况的区域。")]),a._v(" "),r("h2",{attrs:{id:"jvm栈（jvm-stacks）"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#jvm栈（jvm-stacks）"}},[a._v("#")]),a._v(" JVM栈（JVM Stacks）")]),a._v(" "),r("p",[a._v("与程序计数器一样，Java虚拟机栈（Java Virtual Machine Stacks）也是线程私有的，它的生命周期与线程相同。虚拟机栈描述的是Java方法执行的内存模型：每个方法被执行的时候都会同时创建一个栈帧（Stack Frame）用于存储局部变量表、操作栈、动态链接、方法出口等信息。每一个方法被调用直至执行完成的过程，就对应着一个栈帧在虚拟机栈中从入栈到出栈的过程。")]),a._v(" "),r("p",[a._v("局部变量表存放了编译期可知的各种基本数据类型（boolean、byte、char、short、int、float、long、double）、对象引用（reference类型，它不等同于对象本身，根据不同的虚拟机实现，它可能是一个指向对象起始地址的引用指针，也可能指向一个代表对象的句柄或者其他与此对象相关的位置）和returnAddress类型（指向了一条字节码指令的地址）。")]),a._v(" "),r("p",[a._v("其中64位长度的long和double类型的数据会占用2个局部变量空间（Slot），其余的数据类型只占用1个。局部变量表所需的内存空间在编译期间完成分配，当进入一个方法时，这个方法需要在帧中分配多大的局部变量空间是完全确定的，在方法运行期间不会改变局部变量表的大小。")]),a._v(" "),r("p",[a._v("在Java虚拟机规范中，对这个区域规定了两种异常状况：如果线程请求的栈深度大于虚拟机所允许的深度，将抛出StackOverflowError异常；如果虚拟机栈可以动态扩展（当前大部分的Java虚拟机都可动态扩展，只不过Java虚拟机规范中也允许固定长度的虚拟机栈），当扩展时无法申请到足够的内存时会抛出OutOfMemoryError异常。")]),a._v(" "),r("h2",{attrs:{id:"本地方法栈（native-method-stacks）"}},[r("a",{staticClass:"header-anchor",attrs:{href:"#本地方法栈（native-method-stacks）"}},[a._v("#")]),a._v(" 本地方法栈（Native Method Stacks）")]),a._v(" "),r("p",[a._v("本地方法栈（Native Method Stacks）与虚拟机栈所发挥的作用是非常相似的，其区别不过是虚拟机栈为虚拟机执行Java方法（也就是字节码）服务，而本地方法栈则是为虚拟机使用到的Native方法服务。虚拟机规范中对本地方法栈中的方法使用的语言、使用方式与数据结构并没有强制规定，因此具体的虚拟机可以自由实现它。甚至有的虚拟机（譬如Sun HotSpot虚拟机）直接就把本地方法栈和虚拟机栈合二为一。与虚拟机栈一样，本地方法栈区域也会抛出StackOverflowError和OutOfMemoryError异常。")])],1)}),[],!1,null,null,null);t.default=v.exports}}]);